mapper.select:
SELECT n.notice_no , n.loginID , n.notice_title , DATE_FORMAT(n.notice_created_at, '%y-%m-%d') as notice_created_at , ui.user_type FROM tb_notice as n LEFT JOIN tb_userinfo ui ON n.loginID = ui.loginID
and n.notice_title like concat('%',#{stitle},'%')
ORDER BY notice_no DESC;

mapper.insert:
INSERT INTO tb_notice
(
notice_title
, notice_content
, loginID
, notice_created_at

	, file_no

)
VALUES
(
#{notice_title}
, #{notice_content}
, #{loginID}
, now()

	, #{file_no}

)

INSERT INTO tb_file
(
file_origin
, file_local_path
, file_server_path
, file_size
, file_extension
)
VALUES
(
#{file_origin}
, #{file_local_path}
, #{file_server_path}
, #{file_size}
, #{file_extension}
)

mapper.update:
		UPDATE tb_notice
		SET
		notice_title = #{notice_title}
		, notice_content = #{notice_content}


  		    	, file_no = #{file_no}


   		 		, file_no = null



		WHERE 
		notice_no = #{notice_no}
