mapper.select:
SELECT n.notice_no , n.loginID , n.notice_title , DATE_FORMAT(n.notice_created_at, '%y-%m-%d') as notice_created_at , ui.user_type FROM tb_notice as n LEFT JOIN tb_userinfo ui ON n.loginID = ui.loginID
and n.notice_title like concat('%',#{stitle},'%')
ORDER BY notice_no DESC;

mapper.insert:
INSERT INTO tb_notice
(
notice_title
, notice_content
, loginID
, notice_created_at

	, file_no

)
VALUES
(
#{notice_title}
, #{notice_content}
, #{loginID}
, now()

	, #{file_no}

)

INSERT INTO tb_file
(
file_origin
, file_local_path
, file_server_path
, file_size
, file_extension
)
VALUES
(
#{fileInfo.file_nm}
, #{fileInfo.vrfile_loc}
, #{fileInfo.file_loc}
, #{fileInfo.file_Size}
, #{fileInfo.fileExtension}
)
