mapper.insert:

	   	     SELECT IFNULL(MAX(reply_no),0) +1  FROM tb_question_reply
	   	     FROM tb_question_reply
             WHERE question_no = #{questionNo}




	    insert into tb_question_reply
	                 (  
	                    reply_no
	                  , question_no 
                      , loginID                       
                      , name                   
                      , reply_content                    
                      , reply_regdate
                    ) values (
                       #{selreply}
                     , #{questionNo}
                     , #{loginID}
                     , #{name}
                     , #{replyContent}
                     , now()
                    )

mapper.update:
	     update tb_question_reply
	          set loginID = #{loginID}
                 , question_reply_content = #{replyContent}
                 , question_reply_regdate = now()
	     where question_no = #{questionNo}
