mapper.select:
SELECT ui.loginID, ui.name, s.suggestion_no, s.suggestion_category, s.suggestion_answered, s.suggestion_title, s.suggestion_content, DATE_FORMAT(s.suggestion_created_at , '%Y-%m-%d') as suggestion_created_at FROM tb_suggestion s LEFT OUTER JOIN tb_userinfo ui ON ui.loginID = s.loginID
and s.suggestion_title like concat('%',#{stitle},'%')
ORDER BY s.suggestion_answered DESC limit #{startPoint}, #{pageSize}

mapper.insert:
INSERT INTO tb_suggestion
		(
		suggestion_title
		, suggestion_content
		,suggestion_category
		, loginID
		, suggestion_created_at

			, file_no

		)
		VALUES
		(
		#{suggestionTitle}
		, #{suggestionContent}
		, #{suggestionCategory}
		, #{loginID}
		, now()

			, #{file_no}

		)

INSERT INTO tb_file
		(
		file_origin
		, file_local_path
		, file_server_path
		, file_size
		, file_extension
		)
		VALUES
		(
		#{file_origin}
		, #{file_local_path}
		, #{file_server_path}
		, #{file_size}
		, #{file_extension}
		)
