mapper.select:
SELECT c.course_no, c.loginID, c.course_name, c.course_quota, DATE_FORMAT(c.course_start_date, '%y-%m-%d') as course_start_date, DATE_FORMAT(c.course_end_date, '%y-%m-%d') as course_end_date, COALESCE(sa.survey_no, 0) as survey_no, COALESCE(sa.respondent_count, 0) as respondent_count FROM tb_course c LEFT JOIN ( SELECT sa.course_no, sa.survey_no, COUNT(DISTINCT sa.loginID) as respondent_count FROM tb_survey_answer sa GROUP BY sa.course_no, sa.survey_no ) sa ON c.course_no = sa.course_no WHERE c.loginID = #{loginID}
AND c.course_name LIKE concat('%', #{stitle}, '%')
ORDER BY c.course_no DESC LIMIT #{startpoint}, #{pageSize}

select count(*) from tb_course c
where c.loginID = #{loginID}
and c.course_name like concat('%',#{stitle},'%')
