mapper.select:
SELECT
ui.loginID , ui.name , n.notice_no , n.loginID , n.notice_title , DATE_FORMAT(n.notice_created_at, '%y-%m-%d') as notice_created_at , ui.user_type
FROM tb_notice n LEFT OUTER JOIN tb_userinfo ui ON ui.loginID = n.loginID
AND n.notice_title LIKE concat('%',#{stitle},'%')
AND ui.user_type = #{user_type}
ORDER BY notice_no DESC LIMIT #{startpoint}, #{pageSize}

SELECT COUNT(*) FROM tb_notice n INNER JOIN tb_userinfo ui ON ui.loginID = n.loginID
AND n.notice_title LIKE concat('%',#{stitle},'%')
AND ui.user_type = #{user_type}

mapper.insert:


    INSERT INTO tb_notice
	(
		  notice_title
		, notice_content
		, loginID
		, notice_created_at

			, file_no


		)
		VALUES (
		  #{noticeTitle}
		, #{noticeContent}
		, #{loginID}
		, now()

		, #{file_no}

	)

INSERT INTO tb_file
		(
		file_origin
		, file_local_path
		, file_server_path
		, file_size
		, file_extension
		)
		VALUES
		(
		#{file_origin}
		, #{file_local_path}
		, #{file_server_path}
		, #{file_size}
		, #{file_extension}
		)

mapper.delete:
DELETE FROM tb_notice
WHERE notice_no = #{pNoticeNo}	
AND loginID = #{loginID}
